//-- Functional IF statement

const { isCallable } = require( './callable.cjs' );

function FIF(
  condition,
  onTrue = () => 1,
  onFalse = () => 0
) {

  if ( isCallable(condition) ) {
    if ( condition() ) {
      if (isCallable(onTrue)) { return onTrue() }
      return onTrue
    }
    
    if (isCallable(onFalse)) { return onFalse() }
    return onFalse
  }

  if ( condition ) {
    if (isCallable(onTrue)) { return onTrue() }
    return onTrue
  }
  
  if (isCallable(onFalse)) { return onFalse() }
  return onFalse
  
}

module.exports = {
  FIF
}