const { createIdentifier } = require( '../utils/md5.cjs' );
const ID = createIdentifier();

class Node {

  constructor(body) {
    this.body = body;
    this.id = ID.generate('node');
  }

  get = () => this.body;
  set = (value) => this.body = value;
  
}

function createNode(body = []) {
  return new Node(body);
} 

module.exports = {
  createNode,
}