class Stack {

  constructor(body) {
    this.body = body;
  }

  add = (value) => this.body.push(value);
  remove = () => this.body.pop();
  get = () => this.body;
  set = (body) => this.body = body;
  
}

function createStack(body = []){
   return new Stack(body);
}

module.exports = {
  createStack
}